//取消请求        Fluttertoast.cancel();        return new Response(data: {'code': 'CANCEL','message':null}, requestOptions: _cancelToken.requestOptions!);      }      if(e.type==DioErrorType.connectTimeout){        Fluttertoast.showToast(msg: '请求超时');        return new Response(data: {'code': 'TIME_OUT','message':'请求超时'}, requestOptions: _cancelToken.requestOptions!);      }      //处理请求返回的鉴权失败      if (e.response != null && e.response!.statusCode == 401) {        // LoginUtil.instance.loginNativePage();      } else if (e.response != null && e.response!.statusCode == 500) {        String message = '服务器错误';        return new Response(            statusCode: 500,data: {'code':'error','message':message}, requestOptions: _cancelToken.requestOptions!);      }else if (e.response != null && e.response!.statusCode == 422) {        String message = '未知错误';        Map <dynamic, dynamic>arg = e.response!.data['data'];        arg.forEach((key, value) {          if(arg.values.length > 0){            if(arg.values.first.length>0){              message  = arg.values.first[arg.values.first.length>1?1:0];            }          }        });        print('message=== ${message.toString()}');        return new Response(            statusCode: 422,data: {'code':'error','message':message}, requestOptions: _cancelToken.requestOptions!);      }      return e.response != null ? e.response! : new Response(statusCode: 666, data: {'code': 'no_response'}, requestOptions: _cancelToken.requestOptions!);    }  }  Future<Response> get(api, {Map<String,      dynamic>? parameters, Options? options, bool showLoading = false,BuildContext? context}) async {    assert(!showLoading||context!=null);    String tmpApi = api;    String? baseApi = ApiConfig.baseUrl;    if(baseApi.isEmpty){      baseApi = ApiConfig.baseUrl;    }    api = baseApi + api;    if (showLoading) {      // CommonWidgets.showLoadingWidgets(context!);    }    CancelToken _cancelToken = new CancelToken();    _cancelTokenMap[tmpApi]=_cancelToken;    try {      Response response = await _getDio().get(api, queryParameters: parameters, options: options,cancelToken: _cancelToken);      if (showLoading) {        // CommonWidgets.dismissLoading();      }      _cancelTokenMap.remove(tmpApi);      return response;    } on DioError catch (e) {      if (showLoading) {        // CommonWidgets.dismissLoading();      }      _cancelTokenMap.remove(tmpApi);      if(e.type==DioErrorType.cancel){//取消请求        Fluttertoast.cancel();        return new Response(data: {'code': 'CANCEL','message':null}, requestOptions: _cancelToken.requestOptions!);      }      if(e.type==DioErrorType.connectTimeout){        // Fluttertoast.showToast(msg: '请求超时');        return new Response(data: {'code': 'TIME_OUT','message':'请求超时'}, requestOptions: _cancelToken.requestOptions!);      }      //处理请求返回的鉴权失败      if (e.response != null && e.response!.statusCode == 401) {        // LoginUtil.instance.loginNativePage();      } else if (e.response != null && e.response!.statusCode == 500) {        String message = '服务器错误';        return new Response(statusCode: 500,data: {'code':'error','message':message}, requestOptions: _cancelToken.requestOptions!);      }else if (e.response != null && e.response!.statusCode == 422) {        String message = '未知错误';        Map <dynamic, dynamic>arg = e.response!.data['data'];        arg.forEach((key, value) {          if(arg.values.length > 0){            message  = arg.values.first[0];          }        });        print('message=== ${message.toString()}');        return new Response(statusCode: 422,data: {'code':'error','message':message}, requestOptions: _cancelToken.requestOptions!);      }      return e.response != null ? e.response! : new Response(statusCode: 666, data: {'code': 'no_response'}, requestOptions: _cancelToken.requestOptions!);    }  }  void cancel(String cancelApi){    if(!_cancelTokenMap.containsKey(cancelApi)){      return;    }    CancelToken? cancelToken=_cancelTokenMap[cancelApi];    if(cancelToken!=null&&!cancelToken.isCancelled){      cancelToken.cancel('$cancelApi is cancelled');    }  }  void dispose(){    _cancelTokenMap.clear();  }  // 下载  static Future<Response> download(String url, String savePath,      Function(int count, int total) progressCallback) async {    return Dio().download(url, savePath, onReceiveProgress: progressCallback);  }}