import 'package:shared_preferences/shared_preferences.dart';class ManagerUtils{  static ManagerUtils get instance => _getInstance();  static ManagerUtils? _managerUtils;  SharedPreferences? _prefs;  static ManagerUtils _getInstance() {    if (_managerUtils == null) {      _managerUtils = new ManagerUtils._init();    }    return _managerUtils!;  }  ManagerUtils._init(){    if(_prefs==null){      _initSharedPreferences();    }  }  init(){    _initSharedPreferences();  }  Future<void> _initSharedPreferences() async {    _prefs = await SharedPreferences.getInstance();  }  saveSeriesNumber(String? number){    if (number == null) return;    _prefs?.setString("series_number", number);  }  String? getSeriesNumber(){    return _prefs?.getString('series_number');  }  saveHttpToken(String? token){    if(token == null) return;    _prefs?.setString("http_token", token);  }  String? getHttpToken(){    return _prefs?.getString('http_token');  }  saveRate(int? rate){    if (rate == null) return;    _prefs?.setInt('rate', rate);  }  int? getRate(){    return _prefs?.getInt('rate');  }  void clear(){    _prefs?.clear();  }  T? get<T> (String key) {    var result = _prefs?.get(key);    if (result != null) {      return result as T;    }    return null;  }}