import 'dart:ui';import 'package:library_reservation/core/service/config/color_config.dart';import 'package:shared_preferences/shared_preferences.dart';import 'package:library_reservation/core/model/dic_data_value_entity.dart';import 'dart:convert' as convert;import 'common.dart';class ManagerUtils{  static ManagerUtils get instance => _getInstance();  static ManagerUtils? _managerUtils;  SharedPreferences? _prefs;  static ManagerUtils _getInstance() {    if (_managerUtils == null) {      _managerUtils = new ManagerUtils._init();    }    return _managerUtils!;  }  ManagerUtils._init(){    if(_prefs==null){      _initSharedPreferences();    }  }  init(){    _initSharedPreferences();  }  Future<void> _initSharedPreferences() async {    _prefs = await SharedPreferences.getInstance();  }  saveSeriesNumber(String? number){    if (number == null) return;    _prefs?.setString("series_number", number);  }  String? getSeriesNumber(){    return _prefs?.getString('series_number');  }  saveHttpToken(String? token){    if(token == null) return;    _prefs?.setString("http_token", token);  }  String? getHttpToken(){    return _prefs?.getString('http_token');  }  saveRate(int? rate){    if (rate == null) return;    _prefs?.setInt('rate', rate);  }  int? getRate(){    return _prefs?.getInt('rate');  }  saveDicVersion(String? version,String? dicCode){    if(version == null || dicCode == null) return;    _prefs?.setString(dicCode, version);  }  String? getDicVersion(String? dicCode){    if(dicCode == null) return '';    return _prefs?.getString(dicCode);  }  saveDicValue(String? key,String? value){    if(key == null || value == null) return;    _prefs?.setString(key, value);  }  String? getDicValue(String? key){    if(key == null) return '';    return _prefs?.getString(key);  }  DicDataValueEntity? getDicData(){    String version = ManagerUtils.instance.getDicVersion(Common.dic_code)!;    String dicValue = ManagerUtils.instance.getDicValue('${Common.dic_code}$version')!;    return ManagerUtils.instance.getDicValueData(dicValue);  }  DicDataValueEntity? getDicValueData(String? dicValue){    if (dicValue == null) return null;    Map<String, dynamic> user = convert.jsonDecode(dicValue);    DicDataValueEntity dataValueEntity = DicDataValueEntity().fromJson(user);    return dataValueEntity;  }  DicDataValueScanCodeResultCqqrcode? getResultCode(){    return ManagerUtils.instance.getDicData()!.scanCodeResult!.cqqrcode;  }  // 疫苗接种信息  String? getVaccinationStatus(String? key){    if (key == null) return '';    print('vaccinationStatusDic ==== ${ManagerUtils.instance.getResultCode()!}');    return ManagerUtils.instance.getResultCode()!.vaccinationStatus![key];  }  // 加强针接种信息  String? getVaccinationPlusStatus(String? key){    if (key == null) return '';    return ManagerUtils.instance.getResultCode()!.vaccinationPlusStatus![key];  }  // 核酸检测信息  String? getLastRnaResult(String? key){    if (key == null) return '';    return ManagerUtils.instance.getResultCode()!.lastRnaResult![key];  }  // 核酸检测信息-颜色  Color getLastRnaResultColor(int? result){    if (result == null) return ColorConfig.colorFFFFFF;    if (result == 1) return ColorConfig.color0BFF00;    if (result == 2) return ColorConfig.colorFF2F24;    return ColorConfig.colorFFFFFF;  }  // 健康状态信息-图片  String? getHealthStatusImage(String? key){    if (key == null) return '';    return ManagerUtils.instance.getResultCode()!.healthStatus![key]['image'];  }  //  行程核验信息-文本  String? getTravelStatusText(String? key){    if (key == null) return '';    return ManagerUtils.instance.getResultCode()!.travelStatus![key]['text'];  }  void clear(){    _prefs?.clear();  }  T? get<T> (String key) {    var result = _prefs?.get(key);    if (result != null) {      return result as T;    }    return null;  }}