vigator.of(context).pushNamed(SCCodeDialogWidget.routeString);  //   });  //   //  //  // }  bool dialogFlag = false;  @override  Widget build(BuildContext context) {    return Scaffold(        extendBodyBehindAppBar: true,        backgroundColor: Colors.transparent, //把scaffold的背景色改成透明        appBar: AppBar(          elevation: 0,          centerTitle: true,          backgroundColor: Colors.transparent,          //把appbar的背景色改成透明          // elevation: 0,//appbar的阴影          title: Text("扫码助手", style: TextStyleConfig.textFFFFFF_14_bold),          actions: [            GestureDetector(              child: Container(                padding: EdgeInsets.symmetric(horizontal: 16),                child: Icon(                  Icons.settings,                  color: Colors.white,                ),              ),              onTap: () {                //跳转到设置                _starSettring(context);              },            )          ],        ),        body: Center(            child: Column(              crossAxisAlignment: CrossAxisAlignment.stretch,              mainAxisSize: MainAxisSize.min,              children: [                Container(                    height: 470,                    margin: EdgeInsets.symmetric(horizontal: 16),                    padding: EdgeInsets.only(                      top: 157,                      bottom: 40,                    ),                    decoration: BoxDecoration(                        image: DecorationImage(                          image: AssetImage(                            'assets/images/main_middle_head_ground.png',                          ),                          fit: BoxFit.fill,                        )),                    child: Column(mainAxisSize: MainAxisSize.min, children: [                      Expanded(                        child: Image(                            image:                            AssetImage('assets/images/main_scan_ground.png')),                      ),                      // Consumer<TestProviderModel>(builder: (ctx, vm, child) {                      //   return                      MaterialButton(                        child: Image(                          image: AssetImage('assets/images/main_scan_button.png'),                        ),                        onPressed: () {                          _starScan(context);                          print('onPressed');                          //vm.home_defult = false;                          //scan(context);                          //_openAlertDialog(context);                          // print('dd3');                          // Navigator.push(context, MaterialPageRoute(builder: (_) {                          //   return ScanPage();                          // }));                        },                        //   );                        // }                      ),                    ])),                Container(                  margin: EdgeInsets.symmetric(horizontal: 16),                  padding: EdgeInsets.all(16),                  decoration: BoxDecoration(                      image: DecorationImage(                        image: AssetImage('assets/images/main_bottom_ground.png'),                        fit: BoxFit.fill,                      )),                  child: Text(                    Utils.getNoticeText()!,                    style: TextStyleConfig.textFFFFFF_12,                  ),                )              ],            )));  }  void _starSettring(BuildContext context) {    if (ManagerUtils.instance.getSeriesNumber()!.length == 0) {      _openAlertDialog(context);      return;    }    Navigator.of(context).pushNamed(SettingPage.routeString);  }  void _starScan(BuildContext context) {    if (ManagerUtils.instance.getSeriesNumber()!.length == 0) {      _openAlertDialog(context);      return;    }    requestCalendarPermission().then((value) {      if (value) {        Navigator.of(context).push(          MaterialPageRoute(builder: (context) => CodeScannerExample()),        );      }    });  }  Future _openAlertDialog(BuildContext context) async {    return showDialog(      context: context,      barrierDismissible: false,      builder: (BuildContext context) {        return SCSerialDialog(this);      },    );  }  @override  void onCancel() {    // TODO: implement onCancel    dialogFlag = false;  }  @override  void onOk() {    // TODO: implement onOk  }  Future<bool> requestCalendarPermission() async {    //获取当前的权限    var status = await Permission.camera.status;    if (status == PermissionStatus.granted) {//已经授权      return true;    } else {      //未授权则发起一次申请      status = await Permission.camera.request();      if (status == PermissionStatus.granted) {        return true;      } else {        return false;      }    }  }}