import 'package:flutter/material.dart';import 'package:library_reservation/core/service/config/color_config.dart';import 'package:library_reservation/core/service/utils/assets.dart';import 'code_dialogs_main.dart';class SCCodeDialogWidget extends StatefulWidget {  static const String routeString = '/dialog';  const SCCodeDialogWidget({Key? key}) : super(key: key);  @override  State<SCCodeDialogWidget> createState() => _SCCodeDialogWidgetState();}class _SCCodeDialogWidgetState extends State<SCCodeDialogWidget> {  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: ColorConfig.colorB3000000,      body: _buildContent(context),    );  }  Widget _buildContent(BuildContext context){    double width = MediaQuery.of(context).size.width;    double height = MediaQuery.of(context).size.height;    return Container(      width: double.infinity,      height: double.infinity,      child: Column(        mainAxisAlignment: MainAxisAlignment.center,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          Container(            width: width > height ? 607:322,            height: 25,            child: Row(              mainAxisAlignment: MainAxisAlignment.end,              children: [                Text('5秒后关闭',style: TextStyleConfig.textFFFFFF_12,),                SizedBox(width: 7.5,),                GestureDetector(                  child: Image.asset(Assets.close,width: 24,height: 24,fit: BoxFit.cover,),                  onTap: (){                    Navigator.of(context).pop();                  },                ),                SizedBox(width: 8,)              ],            ),          ),          Container(            width: width > height ? 607:322,            height: width > height ? 257:574,            child: SCDialogsDataWidget(isHorizontal: width > height),          ),        ],      ),    );  }}