import 'package:flutter/material.dart';import 'package:library_reservation/core/service/config/color_config.dart';import 'package:library_reservation/core/service/utils/assets.dart';import 'package:library_reservation/core/service/utils/common_widget.dart';class SCDialogsDataWidget extends StatelessWidget {  const SCDialogsDataWidget({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Stack(      children: [        Image.asset(          Assets.bg_vertical,          width: 322,          height: 574,          fit: BoxFit.cover,        ),        Column(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            SCDialogsDataCodeWidget(),            Row(              children: [                SCDialogsDataCommonWidget(                  title: '新冠疫苗',                  imageAsset: Assets.vaccine,                  name: '已接种新冠疫苗',                  subName: '已接种加强针',                )              ],            )          ],        )      ],    );  }}//二维码模块class SCDialogsDataCodeWidget extends StatelessWidget {  const SCDialogsDataCodeWidget({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      width: double.infinity,      height: 431,      padding: EdgeInsets.all(25),      child: Column(        children: [          SizedBox(height: 10),          _buildMarkWidget(),          SizedBox(height: 20),          _buildQRCodeWidget(),          SizedBox(height: 15),          _buildInformationWidget(),          SizedBox(height: 15),          Divider(            height: 0.5,            color: ColorConfig.colorFFFFFF,          ),          SizedBox(height: 15),          _buildTravelWidget()        ],      ),    );  }  //1.风险文案  Widget _buildMarkWidget(){    return Container(      alignment: Alignment.center,      height: 25,      child: Image.asset(        Assets.mark_yellow,      ),    );  }  //2.二维码  Widget _buildQRCodeWidget(){    return Container(      height: 151,      child: Row(        children: [          SizedBox(width: 30),          CommonWidget.buildCachedNetworkImage(            'https://cdn-oss.bigdatacq.com/10000003/959486248906854400.png',            width: 184,            height: 151,            errorImg: Assets.normal_code,            fit: BoxFit.cover,          ),        ],      ),    );  }  //3.基本信息  Widget _buildInformationWidget(){    return Container(      padding: EdgeInsets.only(left: 3),      width: double.infinity,      height: 72,      child: Column(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Text('人员姓名：**国',style: TextStyleConfig.textFFFFFF_12),          Text('证件号码：*************1234',style: TextStyleConfig.textFFFFFF_12),          Text('扫码时间：2021-03-28 19:28:56',style: TextStyleConfig.textFFFFFF_12)        ],      ),    );  }  //4.行程信息  Widget _buildTravelWidget(){    return Container(      padding: EdgeInsets.only(left: 2),      width: double.infinity,      height: 51,      child: Row(        crossAxisAlignment: CrossAxisAlignment.center,        children: [          Image.asset(            Assets.travel_green,          ),          SizedBox(width: 12,),          Container(            alignment: Alignment.centerLeft,            width: 192,            height: double.infinity,            child: Text(              '*14天内到达或途经过存在中风险或高风险地区的城市',              maxLines: 2,              style: TextStyle(                  fontSize: 12,                  color: ColorConfig.colorFFFFFF,                  height: 2.0              ),            ),          )        ],      ),    );  }}//公共模块界面class SCDialogsDataCommonWidget extends StatelessWidget {  final String? title;  final Color? color;  final String? name;  final String? subName;  final String? imageAsset;  const SCDialogsDataCommonWidget({    Key? key,    this.title,    this.color = ColorConfig.colorFFFFFF,    this.name,    this.subName,    this.imageAsset}) : super(key:key);  @override  Widget build(BuildContext context) {    return Container(      width: 148,      height: 115,      child: Row(        crossAxisAlignment: CrossAxisAlignment.end,        children: [          Image.asset(Assets.detection,width: 14,height: 32,),          SizedBox(width: 9,),          Container(            width: 85,            height: 60,            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Text(title!,style: TextStyleConfig.textFFFFFF_14_bold),                Text(name!,style: TextStyleConfig.textFFFFFF_12),                Text(subName!,style: TextStyleConfig.textFFFFFF_12),              ],            ),          )        ],      ),    );  }}