import 'package:flutter/material.dart';import 'package:library_reservation/core/service/config/color_config.dart';//公共模块界面class SCDialogsDataCommonWidget extends StatelessWidget {  final String? title;  final Color? color;  final String? name;  final String? subName;  final String? imageAsset;  const SCDialogsDataCommonWidget({    Key? key,    this.title,    this.color = ColorConfig.colorFFFFFF,    this.name,    this.subName,    this.imageAsset}) : super(key:key);  @override  Widget build(BuildContext context) {    return Container(      width: 161,      height: 128,      alignment: Alignment.center,      child: Row(        crossAxisAlignment: CrossAxisAlignment.end,        mainAxisAlignment: MainAxisAlignment.center,        children: [          Image.asset(imageAsset!,width: 14,height: 32,),          SizedBox(width: 9,),          Container(            width: 85,            height: 60,            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Text(title!,style: TextStyleConfig.textFFFFFF_14_bold),                Text(name!,style: TextStyleConfig.textFFFFFF_12.copyWith(color: color)),                Text(subName!,style: TextStyleConfig.textFFFFFF_12),              ],            ),          )        ],      ),    );  }}